<?php# +----------------------------------------------------------------------# | Author:Stark# +----------------------------------------------------------------------# | Date:2022/11/03# +----------------------------------------------------------------------# | Desc: 路由异常类# +----------------------------------------------------------------------namespace App\WebSocketController\Error;use EasySwoole\Core\Swoole\ServerManager;use App\Utility\Http\Response;use App\WebSocketController\Base;class ErrorMessage extends Base{    public function errorBody()    {        $msgErrorRet = Response::codeNotSatisfiable();        $msgErrorRet['msg'] = 'request data error';        $this->response()->setMessage(json_encode($msgErrorRet));    }}